import React, { useState } from 'react';

const translations = {
    tr: {
      siteTitle: 'Caffe Soso',
      heroTitle: 'Hoşgeldiniz — Caffe Soso',
      heroSubtitle: 'Kalite, sade sunum ve sıcak servis.',
      aboutTitle: 'Hakkımızda',
      aboutText:
        'Tek merkezli, küçük ve özenli bir kafe markasıyız. Tüm içeriklerimiz yerel kalite standartlarına göre hazırlanır.',
      hrTitle: 'İnsan Kaynakları',
      hrText:
        'Açık pozisyonlar ve işe alım süreçleri için lütfen aşağıdaki e-posta adresine başvurun. CV/ön yazı gönderiniz.',
      contactTitle: 'İletişim',
      contactPhone: '+90 216 567 7 567',
      contactEmail: 'info@caffesoso.com',
      address: 'İstanbul — Tek Merkez',
      applyButton: 'Başvuruyu Gönder',
      downloadMenu: 'Menü (PDF)',
      navHome: 'Anasayfa',
      navHR: 'İK',
      navContact: 'İletişim'
    },
    en: {
      siteTitle: 'Caffe Soso',
      heroTitle: 'Welcome — Caffe Soso',
      heroSubtitle: 'Quality, simplicity and warm service.',
      aboutTitle: 'About Us',
      aboutText:
        "We're a single-location, carefully-run café. Everything is prepared to local quality standards.",
      hrTitle: 'Human Resources',
      hrText:
        'For open positions and recruitment, please send your CV and cover letter to the email below.',
      contactTitle: 'Contact',
      contactPhone: '+90 216 567 7 567',
      contactEmail: 'info@caffesoso.com',
      address: 'Istanbul — Single Location',
      applyButton: 'Send Application',
      downloadMenu: 'Menu (PDF)',
      navHome: 'Home',
      navHR: 'HR',
      navContact: 'Contact'
    },
    de: {
      siteTitle: 'Caffe Soso',
      heroTitle: 'Willkommen — Caffe Soso',
      heroSubtitle: 'Qualität, Schlichtheit und herzlicher Service.',
      aboutTitle: 'Über Uns',
      aboutText:
        'Wir sind ein einzelner Standort, sorgfältig geführtes Café. Alles wird nach lokalen Qualitätsstandards zubereitet.',
      hrTitle: 'Personal',
      hrText:
        'Für offene Stellen und Bewerbungsverfahren senden Sie bitte Ihren Lebenslauf und ein Anschreiben an die folgende E-Mail-Adresse.',
      contactTitle: 'Kontakt',
      contactPhone: '+90 216 567 7 567',
      contactEmail: 'info@caffesoso.com',
      address: 'Istanbul — Ein Standort',
      applyButton: 'Bewerbung senden',
      downloadMenu: 'Menü (PDF)',
      navHome: 'Start',
      navHR: 'Personal',
      navContact: 'Kontakt'
    }
  };

export default function App() {
  const [lang, setLang] = useState('tr');
  const t = translations[lang];

  const scrollToId = id => {
    const el = document.getElementById(id);
    if (el) el.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <div>
      {/* tüm App içeriği buraya */}
      <h1>{t.heroTitle}</h1>
      {/* geri kalan JSX */}
    </div>
  );
}
